import React, { useState } from "react";
import { toast } from "react-toastify";
import { registerApi } from "../apis/Api";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes } from "@fortawesome/free-solid-svg-icons";
import { z } from "zod";

const Register = () => {
  const navigate = useNavigate();
  const [UserName, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setconfirmPassword] = useState("");

  const [errors, setErrors] = useState({});

  const clearError = (field) => {
    setErrors((prevErrors) => ({ ...prevErrors, [field]: "" }));
  };

  const changeUserName = (e) => {
    setUserName(e.target.value);
    clearError("UserName");
  };
  const changeEmail = (e) => {
    setEmail(e.target.value);
    clearError("email");
  };
  const changePhoneNumber = (e) => {
    setPhoneNumber(e.target.value);
    clearError("phoneNumber");
  };
  const changePassword = (e) => {
    setPassword(e.target.value);
    clearError("password");
  };
  const changeconfirmPassword = (e) => {
    setconfirmPassword(e.target.value);
    clearError("confirmPassword");
  };

  // Define the Zod validation schema
  const schema = z
    .object({
      UserName: z.string().min(1, { message: "Name is required" }),
      email: z.string().min(1, { message: "Email is required" }),
      phoneNumber: z.string().min(1, { message: "Phone number is required" }),
      password: z.string().min(1, { message: "Password is required" }),
      confirmPassword: z
        .string()
        .min(1, { message: "Confirm Password is required" }),
    })
    .superRefine((data, ctx) => {
      if (data.UserName.length === 0) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "Name is required",
          path: ["UserName"],
        });
      } else if (!/^[A-Za-z\s]+$/.test(data.UserName)) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "Name cannot contain special characters or numbers",
          path: ["UserName"],
        });
      }
      if (data.email.length === 0) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "Email is required",
          path: ["email"],
        });
      } else if (!/\S+@\S+\.\S+/.test(data.email)) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "Invalid email format",
          path: ["email"],
        });
      }

      if (data.phoneNumber.length === 0) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "Phone number is required",
          path: ["phoneNumber"],
        });
      } else if (!/^\d{10}$/.test(data.phoneNumber)) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "Phone number must be 10 digits",
          path: ["phoneNumber"],
        });
      }

      if (data.password.length === 0) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "Password is required",
          path: ["password"],
        });
      } else if (data.password.length < 8) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "Password must be at least 8 characters long",
          path: ["password"],
        });
      }

      if (data.confirmPassword.length === 0) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "Confirm Password is required",
          path: ["confirmPassword"],
        });
      } else if (data.password !== data.confirmPassword) {
        ctx.addIssue({
          code: z.ZodIssueCode.custom,
          message: "Passwords do not match",
          path: ["confirmPassword"],
        });
      }
    });

  const handleSubmit = (e) => {
    e.preventDefault();

    const data = {
      UserName,
      email,
      phoneNumber,
      password,
      confirmPassword,
    };

    // Validate form data using Zod schema
    const result = schema.safeParse(data);
    if (!result.success) {
      const newErrors = {};
      result.error.errors.forEach((err) => {
        console.log(err);
        newErrors[err.path[0]] = err.message;
      });
      setErrors(newErrors);
      return;
    }

    setErrors({}); // Clear errors if validation passes

    registerApi(data)
      .then((res) => {
        if (res.data.success) {
          toast.success(res.data.message);
          navigate("/login");
        } else {
          toast.error(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal Server Error!");
      });
  };

  return (
    <section className="flex min-h-screen items-center justify-center">
      <div className="container py-12 flex justify-center">
        <div className="w-full md:w-3/4 lg:w-2/3 flex flex-col md:flex-row bg-white shadow-lg rounded-lg overflow-hidden">
          <div className="w-full md:w-1/2 bg-gray-100 p-8 flex items-center justify-center">
            <img
              src="https://i.pinimg.com/564x/a7/dd/af/a7ddaf4475ec3822a9b8aebb2956e253.jpg"
              alt="register image"
              className="object-cover h-full"
            />
          </div>
          <div className="w-full md:w-1/2 p-8">
            <div className="relative">
              <FontAwesomeIcon
                icon={faTimes}
                className="absolute top-0 right-0 text-gray-500 cursor-pointer"
                onClick={() => navigate("/")}
              />
            </div>
            <div className="flex justify-center mb-4">
              <i className="fas fa-cubes fa-2x"></i>
            </div>
            <h3 className="text-2xl font-bold text-center mb-4">Register</h3>
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label className="block text-gray-700">Username</label>
                <input
                  onChange={changeUserName}
                  type="text"
                  className="w-full px-4 py-2 mt-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">Email address</label>
                <input
                  onChange={changeEmail}
                  type="email"
                  className="w-full px-4 py-2 mt-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">Phone Number</label>
                <input
                  onChange={changePhoneNumber}
                  type="text"
                  className="w-full px-4 py-2 mt-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">Password</label>
                <input
                  onChange={changePassword}
                  type="password"
                  className="w-full px-4 py-2 mt-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700">Confirm Password</label>
                <input
                  onChange={changeconfirmPassword}
                  type="password"
                  className="w-full px-4 py-2 mt-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
                  required
                />
              </div>
              <div className="mt-6">
                <button
                  type="submit"
                  className="w-full py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                >
                  Register
                </button>
              </div>
            </form>
            <p className="text-center text-gray-500 mt-4">
              Already have an account?{" "}
              <a href="/login" className="text-blue-500 hover:underline">
                Login
              </a>
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Register;
