import React, { useState, useEffect } from "react";
import {
  createDoctorApi,
  getAllDoctorsApi,
  deleteDoctorApi,
  getPaginationApi,
} from "../../apis/Api";
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEye, faEdit, faTrashAlt, faSearch } from "@fortawesome/free-solid-svg-icons";
import Sidebar from "../../components/Sidebar";

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [doctors, setDoctors] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  // Search query
  const [searchQuery, setSearchQuery] = useState("");
  // Filtered doctors
  const filteredDoctors = doctors.filter((person) =>
    person.fullName.toLowerCase().includes(searchQuery.toLowerCase())
  );
  // Get user
  const user = JSON.parse(localStorage.getItem("user"));

  // Doctor details
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [gender, setGender] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [qualification, setQualification] = useState("");
  const [servicesOffered, setServicesOffered] = useState("");

  // Image upload
  const [uploadValidId, setValidId] = useState(null);
  const [previewId, setPreviewId] = useState(null);

  // Image Upload function
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setValidId(file);
    setPreviewId(URL.createObjectURL(file));
  };

  useEffect(() => {
    getPaginationApi(currentPage).then((res) => {
      setDoctors(res.data.doctors);
      setTotalPages(res.data.totalPages);
    });
  }, [currentPage]);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  // Email validation
  const [emailValidationMessage, setEmailValidationMessage] = useState("");

  const validateEmail = (email) => {
    // Simple regex for basic email validation
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!regex.test(email)) {
      setEmailValidationMessage("Invalid email format.");
    } else {
      setEmailValidationMessage(""); // Clear message when valid
    }
  };

  const handleEmailChange = (e) => {
    const emailValue = e.target.value;
    setEmail(emailValue);
    validateEmail(emailValue);
  };

  // Phone number validation
  const [validationMessage, setValidationMessage] = useState("");

  const validatePhoneNumber = (number) => {
    const regex = /^\d{10}$/; // Adjust regex according to your needs
    if (!regex.test(number)) {
      setValidationMessage("Invalid phone number. Must be 10 digits."); // Customize the message
    } else {
      setValidationMessage(""); // Clear message when valid
    }
  };

  const handlePhoneNumberChange = (e) => {
    const number = e.target.value;
    setPhoneNumber(number);
    validatePhoneNumber(number);
  };

  // Submit Function
  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("fullName", fullName);
    formData.append("email", email);
    formData.append("phoneNumber", phoneNumber);
    formData.append("gender", gender);
    formData.append("address", address);
    formData.append("city", city);
    formData.append("state", state);
    formData.append("qualification", qualification);
    formData.append("servicesOffered", servicesOffered);
    formData.append("uploadValidId", uploadValidId);
    formData.append("user", user);

    createDoctorApi(formData)
      .then((res) => {
        if (res.data.success === false) {
          toast.error(res.data.message);
        } else {
          toast.success(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal Server Error!");
      });
  };

  // Delete
  const handleDelete = (id) => {
    // Confirm dialog box
    const confirm = window.confirm(
      "Are you sure you want to delete doctor's information?"
    );
    if (!confirm) {
      return;
    } else {
      deleteDoctorApi(id).then((res) => {
        if (res.data.success === false) {
          toast.error(res.data.message);
        } else {
          toast.success(res.data.message);
          window.location.reload();
        }
      });
    }
  };

  return (
    <>
      <div className="flex flex-row">
        {/* Sidebar */}
        <div className="w-1/4">
          <Sidebar />
        </div>

        {/* Content */}
        <div className="flex-1 p-4">
          <div className="mb-4">
            <h3 className="text-xl font-semibold mb-2">Doctor</h3>
            <p className="text-gray-600">Manage the list of doctors.</p>
          </div>

          {/* Search bar */}
          <div className="flex justify-center mb-4">
            <div className="flex items-center max-w-lg w-full bg-gray-200 rounded-md">
              <span className="px-3 text-gray-500">
                <FontAwesomeIcon icon={faSearch} />
              </span>
              <input
                type="text"
                placeholder="Search by Name"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="flex-1 p-2 bg-gray-200 outline-none"
              />
            </div>
          </div>

          <button
            type="button"
            className="mb-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-700"
            data-bs-toggle="modal"
            data-bs-target="#exampleModal"
          >
            Add Doctors
          </button>

          <div
            className="modal fade"
            id="exampleModal"
            tabIndex="-1"
            aria-labelledby="exampleModalLabel"
            aria-hidden="true"
          >
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h1 className="modal-title text-lg" id="exampleModalLabel">
                    Add new doctor!
                  </h1>
                  <button
                    type="button"
                    className="close"
                    data-bs-dismiss="modal"
                    aria-label="Close"
                  >
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                <div className="modal-body">
                  <label className="block mb-2">FullName</label>
                  <input
                    onChange={(e) => setFullName(e.target.value)}
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                    type="text"
                  />

                  <label className="block mb-2">Email</label>
                  <input
                    onChange={handleEmailChange}
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                    type="email"
                    id="email"
                  />
                  {emailValidationMessage && (
                    <div className="text-red-500 mb-2">
                      {emailValidationMessage}
                    </div>
                  )}

                  <label className="block mb-2">Phone Number</label>
                  <input
                    onChange={handlePhoneNumberChange}
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                    type="number"
                    id="phoneNumber"
                  />
                  {validationMessage && (
                    <div className="text-red-500 mb-2">{validationMessage}</div>
                  )}

                  <label className="block mb-2">Gender</label>
                  <select
                    onChange={(e) => setGender(e.target.value)}
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                  >
                    <option value="">Select</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                    <option value="Others">Others</option>
                  </select>

                  <label className="block mb-2">Address</label>
                  <input
                    onChange={(e) => setAddress(e.target.value)}
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                    type="text"
                  />

                  <label className="block mb-2">City</label>
                  <input
                    onChange={(e) => setCity(e.target.value)}
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                    type="text"
                  />

                  <label className="block mb-2">State</label>
                  <select
                    onChange={(e) => setState(e.target.value)}
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                  >
                    <option value="">Select State</option>
                    <option value="Province 1">Province 1</option>
                    <option value="Province 2">Province 2</option>
                    <option value="Bagmati Province">Bagmati Province</option>
                    <option value="Gandaki Province">Gandaki Province</option>
                    <option value="Lumbini Province">Lumbini Province</option>
                    <option value="Karnali Province">Karnali Province</option>
                    <option value="Sudurpashchim Province">
                      Sudurpashchim Province
                    </option>
                  </select>

                  <label className="block mb-2">Qualification/Specialization</label>
                  <input
                    onChange={(e) => setQualification(e.target.value)}
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                    type="text"
                  />

                  <label className="block mb-2">Services Offered</label>
                  <textarea
                    onChange={(e) => setServicesOffered(e.target.value)}
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                  ></textarea>

                  <label className="block mb-2">Profile Picture</label>
                  <input
                    onChange={handleImageUpload}
                    className="w-full mb-4 p-2 border border-gray-300 rounded"
                    type="file"
                  />
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="px-4 py-2 bg-gray-300 text-gray-700 rounded"
                    data-bs-dismiss="modal"
                  >
                    Close
                  </button>
                  <button
                    onClick={handleSubmit}
                    type="button"
                    className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-700"
                    data-bs-dismiss="modal"
                  >
                    Save changes
                  </button>
                </div>
              </div>
            </div>
          </div>

          <div className="overflow-x-auto">
            <table className="min-w-full bg-white">
              <thead className="bg-gray-800 text-white">
                <tr>
                  <th className="w-1/12 py-2 px-3">SN</th>
                  <th className="w-2/12 py-2 px-3">Name</th>
                  <th className="w-2/12 py-2 px-3">Email</th>
                  <th className="w-2/12 py-2 px-3">Phone Number</th>
                  <th className="w-1/12 py-2 px-3">Gender</th>
                  <th className="w-2/12 py-2 px-3">Address</th>
                  <th className="w-2/12 py-2 px-3">Qualification</th>
                  <th className="w-2/12 py-2 px-3">Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredDoctors.map((person, index) => (
                  <tr key={person._id} className="border-b">
                    <td className="py-2 px-3">{index + 1}</td>
                    <td className="py-2 px-3">{person.fullName}</td>
                    <td className="py-2 px-3">{person.email}</td>
                    <td className="py-2 px-3">{person.phoneNumber}</td>
                    <td className="py-2 px-3">{person.gender}</td>
                    <td className="py-2 px-3">{person.address}</td>
                    <td className="py-2 px-3">{person.qualification}</td>
                    <td className="py-2 px-3 flex space-x-2">
                      <Link
                        to={`/admin/view/${person._id}`}
                        className="text-blue-500"
                      >
                        <FontAwesomeIcon icon={faEye} />
                      </Link>
                      <Link
                        to={`/admin/edit/${person._id}`}
                        className="text-yellow-500"
                      >
                        <FontAwesomeIcon icon={faEdit} />
                      </Link>
                      <button
                        onClick={() => handleDelete(person._id)}
                        className="text-red-500"
                      >
                        <FontAwesomeIcon icon={faTrashAlt} />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="mt-4">
            <nav aria-label="Page navigation">
              <ul className="flex justify-center">
                <li className={`page-item ${currentPage <= 1 ? "disabled" : ""}`}>
                  <button
                    className="px-3 py-1 border rounded-l-md bg-gray-200 text-gray-700 hover:bg-gray-300"
                    onClick={() => handlePageChange(currentPage - 1)}
                    aria-label="Previous"
                  >
                    &laquo;
                  </button>
                </li>
                {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                  <li
                    key={page}
                    className={`page-item ${currentPage === page ? "active" : ""}`}
                  >
                    <button
                      className={`px-3 py-1 border ${currentPage === page ? "bg-gray-700 text-white" : "bg-gray-200 text-gray-700 hover:bg-gray-300"}`}
                      onClick={() => handlePageChange(page)}
                    >
                      {page}
                    </button>
                  </li>
                ))}
                <li className={`page-item ${currentPage >= totalPages ? "disabled" : ""}`}>
                  <button
                    className="px-3 py-1 border rounded-r-md bg-gray-200 text-gray-700 hover:bg-gray-300"
                    onClick={() => handlePageChange(currentPage + 1)}
                    aria-label="Next"
                  >
                    &raquo;
                  </button>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      </div>
    </>
  );
};

export default AdminDashboard;
