import React, { useState } from "react";
import { toast } from "react-toastify";
import { registerApi } from "../apis/Api";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faClose } from "@fortawesome/free-solid-svg-icons";

const Register = () => {
  const navigate = useNavigate();
  const [UserName, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setconfirmPassword] = useState("");

  const changeUserName = (e) => {
    setUserName(e.target.value);
  };
  const changeEmail = (e) => {
    setEmail(e.target.value);
  };
  const changePhoneNumber = (e) => {
    setPhoneNumber(e.target.value);
  };
  const changePassword = (e) => {
    setPassword(e.target.value);
  };

  const changeconfirmPassword = (e) => {
    setconfirmPassword(e.target.value);
  };

  // Validation functions
  const isValidEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  const isValidPhoneNumber = (number) => /^\d{10}$/.test(number);
  const isValidPassword = (password) => {
    const regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*])(?=.{8,12})/;
    return regex.test(password);
  };
  

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check for empty fields, added as part of required field validation
    if (!UserName || !email || !phoneNumber || !password || !confirmPassword) {
      toast.error("Please fill in all fields.");
      return;
    }

    if (!isValidEmail(email)) {
      toast.error("Invalid email format.");
      return;
    }

    if (!isValidPhoneNumber(phoneNumber)) {
      toast.error("Phone number must be 10 digits.");
      return;
    }

    if (!isValidPassword(password)) {
      toast.error("Password must be at least 8 characters long.");
      return;
    }

    if (password !== confirmPassword) {
      toast.error("Passwords do not match.");
      return;
    }

    const data = {
      UserName,
      email,
      phoneNumber,
      password,
      confirmPassword,
    };

    registerApi(data)
      .then((res) => {
        if (res.data.success) {
          toast.success(res.data.message);
          navigate("/login");
        } else {
          toast.error(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal Server Error!");
      });
  };

  return (
    <>
      <section className="row min-vh-100 align-items-center">
        <div className="container py-5 d-flex justify-content-center">
          <div className="col col-md-10 col-sm-12 col-lg-8 fixed-container">
            <div className="card d-flex align-items-center shadow-lg">
              <div className="row">
                <div className="col-md-6 col-lg-6 order-md-1 order-lg-1 mb-4">
                  <img
                    src="https://i.pinimg.com/564x/a7/dd/af/a7ddaf4475ec3822a9b8aebb2956e253.jpg"
                    alt="register image"
                    className="img-fluid"
                    style={{ marginTop: "100px" }}
                  />
                </div>
                <div>
                  <a
                    className="position-absolute top-0 end-0 m-2 text-black"
                    style={{ cursor: "pointer" }}
                    onClick={() => navigate("/")}
                  >
                    <FontAwesomeIcon icon={faClose} />
                  </a>
                </div>

                <div className="col-md-6 col-lg-6 order-lg-3 d-flex align-items-center">
                  <div className="card-body p-5 p-lg-5 text-black">
                    <form onSubmit={handleSubmit}>
                      <div className="mb-4 d-flex justify-content-center">
                        <i className="fas fa-cubes fa-2x me-2"></i>
                      </div>

                      <h3 className="fw-bold d-flex justify-content-center">
                        Register
                      </h3>

                      {/* Username */}
                      <div className="form-outline mb-2">
                        <label className="form-label">Username</label>
                        <input
                          onChange={changeUserName}
                          type="text"
                          className="form-control form-control-lg border-2 border-black"
                          required
                        />
                      </div>
                      {/* Email */}
                      <div className="form-outline mb-2">
                        <label className="form-label">Email address</label>
                        <input
                          onChange={changeEmail}
                          type="email"
                          className="form-control form-control-lg border-2 border-black"
                          required
                        />
                      </div>
                      {/* Phone Number */}
                      <div className="form-outline mb-2">
                        <label className="form-label">Phone Number</label>
                        <input
                          onChange={changePhoneNumber}
                          type="number"
                          className="form-control form-control-lg border-2 border-black"
                          required
                        />
                      </div>

                      {/* Password */}
                      <div className="form-outline mb-2">
                        <label className="form-label">Password</label>
                        <input
                          onChange={changePassword}
                          type="password"
                          className="form-control form-control-lg border-2 border-black w-full"
                          required
                        />
                      </div>
                      {/* Confirm Password */}
                      <div className="form-outline mb-2">
                        <label className="form-label">Confirm Password</label>
                        <input
                          onChange={changeconfirmPassword}
                          type="password"
                          className="form-control form-control-lg border-2 border-black"
                          required
                        />
                      </div>

                      {/* Register button */}
                      <div className="pt-1 mb-3 d-flex text-center justify-content-center">
                        <button
                          className="btn w-50 mb-2 btn-dark"
                          type="submit"
                        >
                          Register
                        </button>
                      </div>

                      <p className="mb-5 pb-lg-2 d-flex justify-content-center">
                        Already have an account?{" "}
                        <a
                          href="/login"
                          className="text-decoration-none text-black"
                        >
                          Login
                        </a>
                      </p>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Register;
